import React from "react";

import useFetch from "hooks/useFetch";

const AddToFavorites = ({ isFavorited, favoritesCount, articleSlug }) => {

  const apiUrl = `/articles/${articleSlug}/favorite`;
  const [{ response }, doFetch] = useFetch(apiUrl);
  const favoritesCountWithResponse = response 
    ? response.article.favoritesCount
    : favoritesCount;
  const isFavoritesWithResponse = response
    ? response.article.favorited
    : isFavorited;
    
  const buttonClasses = ['btn', 'btn-sm'];

  if (isFavoritesWithResponse) {
    buttonClasses.push('btn-primary');
  } else {
    buttonClasses.push('btn-outline-primary');
  }

  const handleLike = (event) => {
    event.preventDefault();
    doFetch({
      method: isFavoritesWithResponse ? "delete" : "post"
    });
  }

  return (
    <button className={buttonClasses.join(' ')} onClick={handleLike}>
      <i className="ion-heart"></i>
      <span>&nbsp; {favoritesCountWithResponse}</span>
    </button>
  )
}

export default AddToFavorites;